import React, { Component } from "react";
import {Link} from "react-router-dom";


class Pelicula extends Component {
    constructor (props) {
        super(props);
        this.state = {
            id: props.id,
            title: props.title,
            img: props.img,
        }
    }

    render () {
        return (
            this.props ? (
                <article className='character-card'>
                    <img src={this.state.img} alt={this.state.title} />
                    <h4>{this.state.name}</h4>
                    <h4>Estado: {this.state.estado}</h4>
                    <h4>Especie: {this.state.especie}</h4>
                    <Link to={`character/${this.state.id}`}>
                        <button>Ir a detalle</button> <br />
                    </Link>
                    <button onClick={() => this.cambio()}>
                        {this.state.info ? 'Ver más' : 'Ver menos'}
                    </button>
                    <div className={`${this.state.info ? 'hidden' : 'visible'}`}>
                        <p>{this.state.extra}</p>
                    </div>
                    <p className='delete'>Borrar</p>                

                </article>
            ) : (
                <h1>Cargando...</h1>
            )
        );
        }
}

export default Pelicula;
