import React, { Component } from "react";
import {Link} from "react-router-dom";
import "./CharacterCard.css";


class CharacterCard extends Component {
    constructor (props) {
        super(props);
        this.state = {
            id: props.id,
            estado: props.estado,
            especie: props.especie,
            name: props.name,
            img: props.img,
            extra: props.origin.name,
            info: true,
        }
    }
        cambio () {
            if(this.state.info){
                this.setState ({
                    info: false
                })
            } else {
                this.setState ({
                    info: true
                })
            }
        }

    render () {
        return (
            this.props ? (
                <article className='character-card'>
                    <img src={this.state.img} alt={this.state.name} />
                    <h4>{this.state.name}</h4>
                    <h4>Estado: {this.state.estado}</h4>
                    <h4>Especie: {this.state.especie}</h4>
                    <Link to={`character/${this.state.id}`}>
                        <button>Ir a detalle</button> <br />
                    </Link>
                    <button onClick={() => this.cambio()}>
                        {this.state.info ? 'Ver más' : 'Ver menos'}
                    </button>
                    <div className={`${this.state.info ? 'hidden' : 'visible'}`}>
                        <p>{this.state.extra}</p>
                    </div>
                    <p className='delete'>Borrar</p>                

                </article>
            ) : (
                <h1>Cargando...</h1>
            )
        );
        }
}

export default CharacterCard;
